mapper.select:
	select * from tb_import_tables 

		1=1

     and src_db= #{src_db,jdbcType=VARCHAR}


      and src_table=#{src_table,jdbcType=VARCHAR}



     and src_db_type = #{src_db_type,jdbcType=VARCHAR}


     and dbname =  #{dbname,jdbcType=VARCHAR}


     and table_name =  #{table_name,jdbcType=VARCHAR}


    	and table_type =#{table_type,jdbcType=INTEGER}


    	and create_time between #{create_time_from,jdbcType=TIMESTAMP} and #{create_time_to,jdbcType=TIMESTAMP}


		limit #{fromRowId,jdbcType=INTEGER} , #{num,jdbcType=INTEGER}
