mapper.select:
select a.*, b.name, b.type from tb_task_history a, tb_mr_task b
where
	a.mr_task_id = b.id

		and result_status = #{type}

order by id desc limit #{offset,jdbcType=INTEGER} , #{limit,jdbcType=INTEGER}

mapper.insert:
insert into tb_task_history (id, mr_task_id, create_time, 
  update_time, begin_time, end_time, 
  result_status, table_stage_info, result
  )
values (#{id,jdbcType=INTEGER}, #{mr_task_id,jdbcType=INTEGER}, #{create_time,jdbcType=TIMESTAMP}, 
  #{update_time,jdbcType=TIMESTAMP}, #{begin_time,jdbcType=TIMESTAMP}, #{end_time,jdbcType=TIMESTAMP}, 
  #{result_status,jdbcType=BIT}, #{table_stage_info,jdbcType=LONGVARCHAR}, #{result,jdbcType=LONGVARCHAR}
  )
